import 'package:firebase_auth/firebase_auth.dart';
import 'package:google_sign_in/google_sign_in.dart';
import 'package:flutter/foundation.dart';

class AuthService {
  final FirebaseAuth _auth = FirebaseAuth.instance;
  // Provide the server client ID (Web OAuth client) so that an ID token is
  // consistently returned across platforms. This value is generated by
  // Firebase and present in android/app/google-services.json under
  // oauth_client (client_type 3). On Android, the Google Services plugin
  // exposes this value as default_web_client_id, but passing it explicitly
  // avoids platform discrepancies.
  final GoogleSignIn _googleSignIn = GoogleSignIn(
    serverClientId:
        '611897330278-l1he1ddugm2tpaqq275diaiivsuf9d01.apps.googleusercontent.com',
  );

  User? get currentUser => _auth.currentUser;
  Stream<User?> get authStateChanges => _auth.authStateChanges();

  Future<User?> signInWithGoogle() async {
    try {
      final GoogleSignInAccount? googleUser = await _googleSignIn.signIn();
      if (googleUser == null) return null;

      final GoogleSignInAuthentication googleAuth =
          await googleUser.authentication;
      final credential = GoogleAuthProvider.credential(
        accessToken: googleAuth.accessToken,
        idToken: googleAuth.idToken,
      );

      final userCredential = await _auth.signInWithCredential(credential);
      return userCredential.user;
    } on FirebaseAuthException catch (e) {
      debugPrint(
        'FirebaseAuthException during Google sign-in: ${e.code} ${e.message}',
      );
      rethrow;
    } catch (e) {
      debugPrint('Unknown error during Google sign-in: $e');
      rethrow;
    }
  }

  Future<void> signOut() async {
    await _googleSignIn.signOut();
    await _auth.signOut();
  }
}
